import React, { useEffect, useState } from "react";
import axios from "axios";
import MapComponent from "./components/MapComponent";

function App() {
  const [cars, setCars] = useState([]);
  const [selectedCar, setSelectedCar] = useState(null);
  const [borrowedCar, setBorrowedCar] = useState(null);
  const [message, setMessage] = useState("");

  // Fetch cars and borrowed car on mount
  useEffect(() => {
    axios
      .get("http://localhost:5001/api/cars/cars")
      .then((response) => setCars(response.data))
      .catch((error) => console.error("Error fetching cars:", error));

    axios
      .get("http://localhost:5001/api/rentals/rent")
      .then((response) => setBorrowedCar(response.data))
      .catch((error) => console.error("Error fetching borrowed car:", error));
  }, []);

// Handle borrowing a car
const handleBorrowCar = async (car) => {
  try {
    const token = localStorage.getItem("token"); // Lấy token từ localStorage (hoặc nơi khác)
    if (!token) {
      setMessage("Bạn cần đăng nhập để mượn xe.");
      return;
    }

    const currentTime = new Date().toISOString(); // Lấy thời gian hiện tại
    const response = await axios.post(
      "http://localhost:5001/api/rentals/rent",
      {
        carId: car._id,
        startTime: currentTime,
      },
      {
        headers: {
          Authorization: `Bearer ${token}`, // Gửi token trong header
        },
      }
    );

    setMessage(response.data.message);
    setBorrowedCar({
      car,
      startTime: currentTime, // Gán thời gian mượn xe
    });

    // Cập nhật trạng thái của xe trong danh sách
    setCars((prevCars) =>
      prevCars.map((c) =>
        c._id === car._id ? { ...c, available: false, borrowTime: currentTime } : c
      )
    );
  } catch (error) {
    setMessage(error.response?.data?.message || "Lỗi khi mượn xe.");
  }
};


  // Handle returning a car
  const handleReturnCar = async () => {
    if (!borrowedCar) {
      setMessage("Không có xe nào để trả.");
      return;
    }

    try {
      const currentTime = new Date().toISOString(); // Lấy thời gian hiện tại
      const response = await axios.post("http://localhost:5001/api/rentals/return", {
        carId: borrowedCar.car._id,
        endTime: currentTime,
      });

      setMessage(response.data.message);
      setBorrowedCar(null);

      // Cập nhật trạng thái của xe trong danh sách
      setCars((prevCars) =>
        prevCars.map((c) =>
          c._id === borrowedCar.car._id ? { ...c, available: true, borrowTime: null } : c
        )
      );
    } catch (error) {
      setMessage(error.response?.data?.message || "Lỗi khi trả xe.");
    }
  };

  return (
    <div style={{ display: "flex", height: "100vh", width: "100vw" }}>
      <div style={{ flex: 1, height: "100%" }}>
        <MapComponent cars={cars} selectedCar={selectedCar} />
      </div>
      <div
        style={{
          flex: 1,
          height: "100%",
          overflowY: "scroll",
          padding: "20px",
          borderLeft: "2px solid #ccc",
        }}
      >
        <h2>Danh sách xe</h2>
        {cars.map((car) => (
          <div
            key={car._id}
            style={{
              padding: "10px",
              border: "1px solid #ccc",
              marginBottom: "10px",
              cursor: "pointer",
              backgroundColor: car.available ? "#e0f7e0" : "#ffebee",
            }}
            onClick={() => setSelectedCar(car)}
          >
            <h3>{car.brand} {car.model}</h3>
            <p>{car.licensePlate}</p>
            <p style={{ fontWeight: "bold", color: car.available ? "green" : "red" }}>
              {car.available ? "Có sẵn" : "Không có sẵn"}
            </p>
            {car.available ? (
              <button onClick={() => handleBorrowCar(car)}>Mượn Xe</button>
            ) : (
              <p>
                Đã mượn lúc:{" "}
                {car.borrowTime
                  ? new Date(car.borrowTime).toLocaleString()
                  : "Không rõ thời gian"}
              </p>
            )}
          </div>
        ))}
        {borrowedCar && (
          <div>
            <h3>Xe bạn đã mượn:</h3>
            <p>{borrowedCar.car.brand} {borrowedCar.car.model}</p>
            <p>Bắt đầu mượn lúc: {new Date(borrowedCar.startTime).toLocaleString()}</p>
            <button onClick={handleReturnCar}>Trả Xe</button>
          </div>
        )}
        {message && <p>{message}</p>}
      </div>
    </div>
  );
}

export default App;
