import MapComponent from "./components/MapComponent";
import React, { useState, useEffect } from "react";
import axios from "axios";
import { Container, Row, Col, Button, Form, ListGroup, Alert } from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [cars, setCars] = useState([]);
  const [selectedCar, setSelectedCar] = useState(null);
  const [borrowedCar, setBorrowedCar] = useState(null);
  const [message, setMessage] = useState("");
  const [username, setUsername] = useState(""); 
  const [password, setPassword] = useState("");
  const [loggedInUser, setLoggedInUser] = useState(null); 
  const [stations, setStations] = useState([]);
  const [map, setMap] = useState(null); // State for storing map

  // Fetch cars, borrowed car, and stations on mount
  useEffect(() => {
    axios
      .get("http://localhost:5001/api/cars/cars")
      .then((response) => setCars(response.data))
      .catch((error) => console.error("Error fetching cars:", error));

    axios
      .get("http://localhost:5001/api/rentals/rent")
      .then((response) => setBorrowedCar(response.data))
      .catch((error) => console.error("Error fetching borrowed car:", error));

    axios
      .get("http://localhost:5001/api/cars/getStations")
      .then((response) => setStations(response.data))
      .catch((error) => console.error("Error fetching stations:", error));
  }, []);

  // Handle login
  const handleLogin = async () => {
    try {
      const response = await axios.post("http://localhost:5001/api/users/login", {
        username,
        password,
      });

      const { token, userId } = response.data;
      localStorage.setItem("token", token);
      localStorage.setItem("userId", userId);
      setLoggedInUser({ username });
      setMessage("Đăng nhập thành công!");
    } catch (error) {
      setMessage(error.response?.data?.message || "Đăng nhập thất bại.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    setLoggedInUser(null);
    setMessage("Đã đăng xuất.");
  };

  // Handle borrowing a car
  const handleBorrowCar = async () => {
    try {
      if (!selectedCar) {
        setMessage("Bạn chưa chọn xe.");
        return;
      }

      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Bạn cần đăng nhập để mượn xe.");
        return;
      }

      const currentTime = new Date().toISOString();
      const response = await axios.post(
        "http://localhost:5001/api/rentals/rent",
        {
          carId: selectedCar._id,
          rentalStart: currentTime,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setMessage(response.data.message);
      setBorrowedCar({
        selectedCar,
        rentalStart: currentTime,
      });

      setCars((prevCars) =>
        prevCars.map((car) =>
          car._id === selectedCar._id
            ? { ...car, available: false, borrowTime: currentTime }
            : car
        )
      );
    } catch (error) {
      setMessage(error.response?.data?.message || "Lỗi khi mượn xe.");
    }
  };

  // Handle returning a car
  const handleReturnCar = async () => {
    try {
      const rentalEnd = new Date().toISOString();
      const carId = selectedCar._id;

      const response = await axios.post(
        "http://localhost:5001/api/rentals/return",
        { carId, rentalEnd },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      const { rental } = response.data;
      const { totalPrice, rentalEnd: returnedTime } = rental;

      setMessage(
        `Xe đã trả thành công vào lúc: ${new Date(returnedTime).toLocaleString()}. Tiền mượn: ${totalPrice.toLocaleString()} VND`
      );

      setBorrowedCar(null);
      setCars((prevCars) =>
        prevCars.map((car) =>
          car._id === selectedCar._id
            ? { ...car, available: true, borrowTime: null }
            : car
        )
      );
    } catch (error) {
      setMessage(error.response?.data?.message || "Lỗi khi trả xe.");
    }
  };

  // Handle selecting a car
  const handleCarSelect = (car) => {
    setSelectedCar(car);
    console.log("Car selected:", car); // Kiểm tra toàn bộ object xe
    console.log("Selected Car ID:", car._id); // Hiển thị ID của xe
    console.log("thoi gian muon:", car.borrowTime);
  };

  // Map Component - Example placeholder for map
  const renderMap = () => {
    return (
      <div style={{ height: "400px", backgroundColor: "#d3d3d3" }}>
        <h4>Map Area</h4>
 {/* Phần bản đồ */}
        <MapComponent cars={cars} selectedCar={selectedCar} stations={stations} />  
      </div>
    );
  };

  return (
    <Container fluid>
      <Row>
        <Col sm={3}>{/* Map Section */}</Col>
        <Col sm={9}>
          <h1 className="my-4">Car Rental Dashboard</h1>

          {/* Login Form */}
          {!loggedInUser ? (
            <Form className="mb-4">
              <Form.Group controlId="username" className="mb-3">
                <Form.Control
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Username"
                />
              </Form.Group>
              <Form.Group controlId="password" className="mb-3">
                <Form.Control
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Password"
                />
              </Form.Group>
              <Button onClick={handleLogin} variant="primary">
                Login
              </Button>
            </Form>
          ) : (
            <div className="mb-4">
              <h2>Welcome, {loggedInUser.username}</h2>
              <Button onClick={handleLogout} variant="secondary">
                Logout
              </Button>
            </div>
          )}

          {/* Display message */}
          {message && <Alert variant="info">{message}</Alert>}

          {/* Car List */}
          <Row className="gy-3">
            {cars.map((car) => (
              <Col sm={12} md={6} lg={4} key={car._id}>
                <Card
                  style={{ cursor: "pointer", borderColor: car.available ? "#198754" : "#dc3545" }}
                  className={`h-100 ${car.available ? "bg-light" : "bg-danger-subtle"}`}
                  onClick={() => {
                    handleCarSelect(car);
                    console.log("Car ID đã chọn:", car._id);
                  }}
                >
                  <Card.Body>
                    <Card.Title>
                      {car.brand} {car.model}
                    </Card.Title>
                    <Card.Text>
                      <strong>Biển số:</strong> {car.licensePlate}
                    </Card.Text>
                    <Card.Text
                      className={
                        car.available ? "text-success fw-bold" : "text-danger fw-bold"
                      }
                    >
                      {car.available ? "Có sẵn" : "Không có sẵn"}
                    </Card.Text>

                    {car.available ? (
                      <Button
                        variant="success"
                        onClick={handleBorrowCar}
                        className="mt-2"
                      >
                        Mượn Xe
                      </Button>
                    ) : (
                      <>
                        <Card.Text>
                          <strong>Đã mượn lúc:</strong> {car.borrowTime ? new Date(car.borrowTime).toLocaleString() : "---"}
                        </Card.Text>
                        {car.borrowedBy === loggedInUser?.id && (
                          <Button
                            variant="danger"
                            onClick={handleReturnCar}
                            className="mt-2"
                          >
                            Trả Xe
                          </Button>
                        )}
                      </>
                    )}
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>

          {/* Borrowed Car */}
          {borrowedCar && (
            <Alert variant="info" className="mt-4">
              <h4>Xe bạn đã mượn:</h4>
              <p>
                <strong>Xe:</strong> {borrowedCar.selectedCar.brand} {borrowedCar.selectedCar.model}
              </p>
              <p>
                <strong>Bắt đầu mượn lúc:</strong> {new Date(borrowedCar.rentalStart).toLocaleString()}
              </p>
              <Button variant="primary" onClick={handleReturnCar}>
                Trả Xe
              </Button>
            </Alert>
          )}

          {/* Additional Message */}
          {message && <Alert variant="warning" className="mt-3">{message}</Alert>}
        </Col>
      </Row>
    </Container>
  );
}


export default App;
