// src/components/MapComponent.js

import React, { useEffect, useRef } from "react";
import L from "leaflet";

function MapComponent() {
  const mapRef = useRef(null); // Khởi tạo ref để tham chiếu đến phần tử div chứa bản đồ

  useEffect(() => {
    // Tạo bản đồ
    const map = L.map(mapRef.current).setView([21.0285, 105.8542], 13); // Vị trí Hà Nội

    // Thêm lớp bản đồ OpenStreetMap
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution:
        '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
    }).addTo(map);

    // Thêm marker tại vị trí Hà Nội
    L.marker([21.0285, 105.8542]).addTo(map).bindPopup("Hà Nội, Việt Nam") .openPopup();    ;

    // Cleanup function để loại bỏ bản đồ khi component bị hủy
    return () => {
      map.remove();
    };
  }, []);

  return <div id="map" ref={mapRef}></div>; // div chứa bản đồ
}

export default MapComponent;
