import React, { useState, useEffect } from "react";
import axios from "axios";
import MapComponent from "./components/MapComponent";

function App() {
  const [cars, setCars] = useState([]);
  const [selectedCar, setSelectedCar] = useState(null);
  const [borrowedCar, setBorrowedCar] = useState(null);
  const [message, setMessage] = useState("");
  const [username, setUsername] = useState(""); // Thêm state cho đăng nhập
  const [password, setPassword] = useState("");
  const [loggedInUser, setLoggedInUser] = useState(null); // Thông tin người dùng đã đăng nhập
  const [stations, setStations] = useState([]);

  // Fetch cars and borrowed car on mount
  useEffect(() => {
    axios
      .get("http://localhost:5001/api/cars/cars")
      .then((response) => setCars(response.data))
      .catch((error) => console.error("Error fetching cars:", error));

    axios
      .get("http://localhost:5001/api/rentals/rent")
      .then((response) => setBorrowedCar(response.data))
      .catch((error) => console.error("Error fetching borrowed car:", error));

    axios
      .get("http://localhost:5001/api/cars/getStations")
      .then((response) => setStations(response.data))
      .catch((error) => console.error("Error fetching stations:", error));
  }, []);

  // Handle login
  const handleLogin = async () => {
    try {
      const response = await axios.post("http://localhost:5001/api/users/login", {
        username,
        password,
      });

      const { token, userId } = response.data;

      // Lưu token vào localStorage
      localStorage.setItem("token", token);
      localStorage.setItem("userId", userId);

      setLoggedInUser({ username }); // Lưu thông tin người dùng
      setMessage("Đăng nhập thành công!");
    } catch (error) {
      setMessage(error.response?.data?.message || "Đăng nhập thất bại.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    setLoggedInUser(null);
    setMessage("Đã đăng xuất.");
  };

  // Handle borrowing a car
  const handleBorrowCar = async () => {
    try {
      if (!selectedCar) {
        setMessage("Bạn chưa chọn xe.");
        return;
      }

      const token = localStorage.getItem("token");
      if (!token) {
        setMessage("Bạn cần đăng nhập để mượn xe.");
        return;
      }

      const currentTime = new Date().toISOString();
      const response = await axios.post(
        "http://localhost:5001/api/rentals/rent",
        {
          carId: selectedCar._id,
          rentalStart: currentTime,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setMessage(response.data.message);
      setBorrowedCar({
        selectedCar,
        rentalStart: currentTime,
      });

      // Cập nhật trạng thái của xe trong danh sách
      setCars((prevCars) =>
        prevCars.map((car) =>
          car._id === selectedCar._id
            ? { ...car, available: false, borrowTime: currentTime }
            : car
        )
      );
    } catch (error) {
      setMessage(error.response?.data?.message || "Lỗi khi mượn xe.");
    }
  };

  // Handle returning a car
  const handleReturnCar = async () => {
    try {
      const rentalEnd = new Date().toISOString();
      const carId = selectedCar._id;

      const response = await axios.post(
        "http://localhost:5001/api/rentals/return",
        { carId, rentalEnd },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      const { rental } = response.data;
      const { totalPrice, rentalEnd: returnedTime } = rental;

      setMessage(
        `Xe đã trả thành công vào lúc: ${new Date(returnedTime).toLocaleString()}. Tiền mượn: ${totalPrice.toLocaleString()} VND`
      );

      setBorrowedCar(null);

      // Cập nhật trạng thái của xe sau khi trả
      setCars((prevCars) =>
        prevCars.map((car) =>
          car._id === selectedCar._id
            ? { ...car, available: true, borrowTime: null }
            : car
        )
      );
    } catch (error) {
      setMessage(error.response?.data?.message || "Lỗi khi trả xe.");
    }
  };

  // Handle selecting a car
  const handleCarSelect = (car) => {
    setSelectedCar(car);
  };

  return (
    <div>
      <h1>Car Rental</h1>

      {/* Login Form */}
      {!loggedInUser ? (
        <div>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
          />
          <button onClick={handleLogin}>Login</button>
        </div>
      ) : (
        <div>
          <h2>Welcome, {loggedInUser.username}</h2>
          <button onClick={handleLogout}>Logout</button>
        </div>
      )}

      {/* Display message */}
      {message && <div>{message}</div>}

      {/* Car Selection */}
      <h3>Available Cars</h3>
      <ul>
        {cars.map((car) => (
          <li
            key={car._id}
            onClick={() => handleCarSelect(car)}
            style={{
              cursor: "pointer",
              backgroundColor: car.available ? "lightgreen" : "lightcoral",
            }}
          >
            {car.brand} {car.model} -{" "}
            {car.available ? "Available" : "Not Available"}
          </li>
        ))}
      </ul>

      {/* Rent and Return buttons */}
      {selectedCar && selectedCar.available && (
        <button onClick={handleBorrowCar}>Rent Car</button>
      )}
      {borrowedCar && borrowedCar.selectedCar._id === selectedCar?._id && (
        <button onClick={handleReturnCar}>Return Car</button>
      )}

      {/* Thêm phần bản đồ */}
      <div style={{ flex: 1, height: "100%", position: "relative" }}>
        <MapComponent cars={cars} selectedCar={selectedCar} stations={stations} />

        {/* Ô thông tin xe đã chọn */}
        {selectedCar && (
          <div
            style={{
              position: "absolute",
              top: 20,
              left: 20,
              padding: "10px",
              backgroundColor: "rgba(255, 255, 255, 0.7)",
              borderRadius: "8px",
              boxShadow: "0 2px 5px rgba(0, 0, 0, 0.2)",
              zIndex: 10,
            }}
          >
            <h4>Thông tin xe</h4>
            <p><strong>Hãng:</strong> {selectedCar.brand}</p>
            <p><strong>Model:</strong> {selectedCar.model}</p>
            <p><strong>Trạng thái:</strong> {selectedCar.available ? "Có sẵn" : "Đã mượn"}</p>
            {selectedCar.borrowTime && (
              <p><strong>Thời gian mượn:</strong> {new Date(selectedCar.borrowTime).toLocaleString()}</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
