import React, { useEffect, useState } from "react";
import axios from "axios";
import MapComponent from "./components/MapComponent";

function App() {
  const [cars, setCars] = useState([]);
  const [selectedCar, setSelectedCar] = useState(null);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [user, setUser] = useState(null);
  const [borrowedCar, setBorrowedCar] = useState(null);
  const [borrowTime, setBorrowTime] = useState("");
  const [returnTime, setReturnTime] = useState("");
  const [message, setMessage] = useState("");

  // Fetch user info on mount
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      axios
        .get("http://localhost:5000/api/users/me", {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => setUser(response.data.user))
        .catch((error) => console.error("Error fetching user info:", error));
    }
  }, []);

  // Handle login
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/auth/login", { email, password });
      localStorage.setItem("token", response.data.token);
      setUser(response.data.user);
      setErrorMessage("");

      // Fetch borrowed car for logged-in user
      const borrowedCarResponse = await axios.get("http://localhost:5000/api/cars/borrowed", {
        headers: { Authorization: `Bearer ${response.data.token}` },
      });
      setBorrowedCar(borrowedCarResponse.data);
    } catch (error) {
      setErrorMessage(error.response?.data?.message || "Lỗi hệ thống");
    }
  };

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    setUser(null);
    setCars([]);
    setSelectedCar(null);
    setBorrowedCar(null);
  };

// Handle borrowing a car
const handleBorrowCar = async () => {
  if (!selectedCar) {
    setMessage("Vui lòng chọn một xe để mượn.");
    return;
  }

  try {
    const token = localStorage.getItem("token");

    // Kiểm tra nếu token không có
    if (!token) {
      setMessage("Vui lòng đăng nhập trước khi mượn xe.");
      return;
    }

    // Lấy thông tin người dùng từ token
    const decodedToken = jwtDecode(token); // Sử dụng thư viện jwt-decode để giải mã token
    const userId = decodedToken.userId; // Lấy userId từ token đã giải mã

    // Lấy thời gian hiện tại
    const startTime = new Date().toISOString();

    // Gửi request mượn xe
    const response = await axios.post(
      "http://localhost:5000/api/borrow/borrow",
      {
        carId: selectedCar._id,
        startTime, // Gửi thời gian thực
        userId, // Thêm userId vào request
      },
      {
        headers: {
          Authorization: `Bearer ${token}`, // Gửi token để backend lấy thông tin user
        },
      }
    );

    // Cập nhật trạng thái
    setMessage(response.data.message);
    setBorrowedCar(response.data.booking);
  } catch (error) {
    setMessage(error.response?.data?.message || "Lỗi khi mượn xe.");
  }
};

  
  // Handle returning a car
  const handleReturnCar = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:5000/api/borrow/return",
        {
          bookingId: borrowedCar._id,
          endTime: returnTime,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setMessage(response.data.message);
      setBorrowedCar(null);
    } catch (error) {
      setMessage(error.response?.data?.message || "Lỗi khi trả xe.");
    }
  };

  // Fetch cars and borrowed car when user is logged in
  useEffect(() => {
    if (user) {
      axios
        .get("http://localhost:5000/api/cars")
        .then((response) => setCars(response.data))
        .catch((error) => console.error("Error fetching cars:", error));

      axios
        .get("http://localhost:5000/api/cars/borrowed", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        })
        .then((response) => setBorrowedCar(response.data))
        .catch((error) => console.error("Error fetching borrowed car:", error));
    }
  }, [user]);

  return (
    <div style={{ display: "flex", height: "100vh", width: "100vw" }}>
      {!user ? (
        <div style={{ flex: 1, display: "flex", justifyContent: "center", alignItems: "center" }}>
          <div style={{ textAlign: "center" }}>
            <h1>Chào mừng!</h1>
            <p>Vui lòng đăng nhập hoặc đăng ký để tiếp tục.</p>
            <form
              onSubmit={handleLogin}
              style={{ padding: "20px", border: "1px solid #ccc", borderRadius: "8px" }}
            >
              <h2>Đăng Nhập</h2>
              <div>
                <label>Email:</label>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
              </div>
              <div>
                <label>Mật khẩu:</label>
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
              </div>
              <button type="submit">Đăng Nhập</button>
              {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
            </form>
            <button style={{ marginTop: "10px" }}>Đăng Ký</button>
          </div>
        </div>
      ) : (
        <>
          <div style={{ flex: 1, height: "100%" }}>
            <MapComponent cars={cars} selectedCar={selectedCar} />
          </div>

          <div
            style={{
              flex: 1,
              height: "100%",
              overflowY: "scroll",
              padding: "20px",
              borderLeft: "2px solid #ccc",
            }}
          >
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
              <h2>Xin chào, {user.name || user.email}!</h2>
              <button onClick={handleLogout}>Đăng Xuất</button>
            </div>
            <h3>Xe bạn đã mượn:</h3>
                              {borrowedCar ? (
                    <div
                      style={{
                        padding: "10px",
                        border: "1px solid #ccc",
                        marginBottom: "10px",
                        backgroundColor: "#fffbea",
                      }}
                    >
                      <h3>{borrowedCar.car.brand} {borrowedCar.car.model}</h3>
                      <p>Biển số: {borrowedCar.car.licensePlate}</p>
                      <p>Thời gian mượn: {new Date(borrowedCar.startTime).toLocaleString()}</p>
                      <button onClick={handleReturnCar}>Trả Xe</button>
                    </div>
                  ) : (
                    <p>Bạn chưa mượn xe nào.</p>
                  )}


            <h2>Danh sách xe</h2>
            {cars.map((car) => (
              <div
                key={car._id}
                style={{
                  padding: "10px",
                  border: "1px solid #ccc",
                  marginBottom: "10px",
                  cursor: "pointer",
                  backgroundColor: car.availability ? "#e0f7e0" : "#ffebee",
                }}
                onClick={() => setSelectedCar(car)}
              >
                <h3>{car.brand} {car.model}</h3>
                <p>{car.licensePlate}</p>
                <p style={{ fontWeight: "bold", color: car.availability ? "green" : "red" }}>
                  {car.availability ? "Có sẵn" : "Không có sẵn"}
                </p>
                {car.availability && (
                  <div>
                    <input
                      type="datetime-local"
                      value={borrowTime}
                      onChange={(e) => setBorrowTime(e.target.value)}
                    />
                    <button onClick={handleBorrowCar}>Mượn Xe</button>
                  </div>
                )}
              </div>
            ))}
            {message && <p>{message}</p>}
          </div>
        </>
      )}
    </div>
  );
}

export default App;
