import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';

const Map = () => {
  // Tọa độ cụ thể của vị trí muốn hiển thị (VD: Hà Nội)
  const position = [21.028511, 105.804817]; // Latitude, Longitude của Hà Nội

  return (
    <MapContainer
      center={position} // Đặt tâm bản đồ tại vị trí
      zoom={13}         // Mức zoom (càng lớn càng gần)
      style={{ height: '500px', width: '100%' }}
    >
      {/* Sử dụng bản đồ OpenStreetMap */}
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution="&copy; OpenStreetMap contributors"
      />
      {/* Đặt Marker tại vị trí */}
      <Marker position={position}>
        <Popup>
          <strong>Hà Nội</strong> <br />
          Đây là vị trí trung tâm Hà Nội.
        </Popup>
      </Marker>
    </MapContainer>
  );
};

export default Map;
