import React, { useEffect, useState } from "react";
import axios from "axios";
import MapComponent from "./components/MapComponent";

function App() {
  const [cars, setCars] = useState([]);
  const [selectedCar, setSelectedCar] = useState(null);
  const [borrowedCar, setBorrowedCar] = useState(null);
  const [message, setMessage] = useState("");
  const [username, setUsername] = useState(""); // Thêm state cho đăng nhập
  const [password, setPassword] = useState("");
  const [loggedInUser, setLoggedInUser] = useState(null); // Thông tin người dùng đã đăng nhập

  // Fetch cars and borrowed car on mount
  useEffect(() => {
    axios
      .get("http://localhost:5001/api/cars/cars")
      .then((response) => setCars(response.data))
      .catch((error) => console.error("Error fetching cars:", error));

    axios
      .get("http://localhost:5001/api/rentals/rent")
      .then((response) => setBorrowedCar(response.data))
      .catch((error) => console.error("Error fetching borrowed car:", error));
  }, []);

  // Handle login
  const handleLogin = async () => {
    try {
      const response = await axios.post("http://localhost:5001/api/users/login", {
        username,
        password,
      });

      const { token, userId } = response.data;

      // Lưu token vào localStorage
      localStorage.setItem("token", token);
      localStorage.setItem("userId", userId);

      setLoggedInUser({ username }); // Lưu thông tin người dùng
      setMessage("Đăng nhập thành công!");
    } catch (error) {
      setMessage(error.response?.data?.message || "Đăng nhập thất bại.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    setLoggedInUser(null);
    setMessage("Đã đăng xuất.");
  };

  // Handle borrowing a car
  const handleBorrowCar = async (car) => {
    try {
        if (!selectedCar) {
          setMessage("Bạn chưa chọn xe.");
          return;
        }
      
        const token = localStorage.getItem("token");
        if (!token) {
          setMessage("Bạn cần đăng nhập để mượn xe.");
          return;
        }




      const currentTime = new Date().toISOString(); // Lấy thời gian hiện tại
      const response = await axios.post(
        "http://localhost:5001/api/rentals/rent",
        {
          carId: selectedCar._id,
          startTime: currentTime,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`, // Gửi token trong header
          },
        }
      );

      setMessage(response.data.message);
      setBorrowedCar({
        selectedCar,
        startTime: currentTime, // Gán thời gian mượn xe
      });

      // Cập nhật trạng thái của xe trong danh sách
      setCars((prevCars) =>
        prevCars.map((car) =>
          car._id === selectedCar._id ? { ...c, available: false, borrowTime: currentTime } : car
        )
      );
    } catch (error) {
      setMessage(error.response?.data?.message || "Lỗi khi mượn xe.");
    }
  };

  return (
    <div style={{ display: "flex", height: "100vh", width: "100vw" }}>
      {/* Góc trên bên phải: Đăng nhập hoặc thông tin người dùng */}
      <div
        style={{
          position: "absolute",
          top: "10px",
          right: "10px",
          padding: "10px",
          backgroundColor: "#fff",
          borderRadius: "8px",
          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
        }}
      >
        {loggedInUser ? (
          <div>
            <p>Xin chào, {loggedInUser.username}</p>
            <button onClick={handleLogout}>Đăng xuất</button>
          </div>
        ) : (
          <div>
            <input
              type="text"
              placeholder="Tên đăng nhập"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={{ marginBottom: "5px" }}
            />
            <br />
            <input
              type="password"
              placeholder="Mật khẩu"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={{ marginBottom: "5px" }}
            />
            <br />
            <button onClick={handleLogin}>Đăng nhập</button>
          </div>
        )}
      </div>

      <div style={{ flex: 1, height: "100%" }}>
        <MapComponent cars={cars} selectedCar={selectedCar} />
      </div>

      <div
        style={{
          flex: 1,
          height: "100%",
          overflowY: "scroll",
          padding: "20px",
          borderLeft: "2px solid #ccc",
        }}
      >
        <h2>Danh sách xe</h2>
        {cars.map((car) => (
          <div
            key={car._id}
            style={{
              padding: "10px",
              border: "1px solid #ccc",
              marginBottom: "10px",
              cursor: "pointer",
              backgroundColor: car.available ? "#e0f7e0" : "#ffebee",
            }}
            onClick={() => setSelectedCar(car)}
          >
            <h3>{car.brand} {car.model}</h3>
            <p>{car.licensePlate}</p>
            <p style={{ fontWeight: "bold", color: car.available ? "green" : "red" }}>
              {car.available ? "Có sẵn" : "Không có sẵn"}
            </p>
            {car.available ? (
              <button onClick={() => handleBorrowCar(car)}>Mượn Xe</button>
            ) : (
              <p>
                Đã mượn lúc:{" "}
                {car.borrowTime
                  ? new Date(car.borrowTime).toLocaleString()
                  : "Không rõ thời gian"}
              </p>
            )}
          </div>
        ))}
        {borrowedCar && (
          <div>
            <h3>Xe bạn đã mượn:</h3>
            <p>{borrowedCar.car.brand} {borrowedCar.car.model}</p>
            <p>Bắt đầu mượn lúc: {new Date(borrowedCar.startTime).toLocaleString()}</p>
            <button onClick={() => handleReturnCar()}>Trả Xe</button>
          </div>
        )}
        {message && <p>{message}</p>}
      </div>
    </div>
  );
}

export default App;
