import React, { useEffect, useRef } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

function MapComponent({ cars }) {
  const mapRef = useRef(null); // Tạo ref cho bản đồ

  useEffect(() => {
    // Tạo bản đồ
    const map = L.map(mapRef.current).setView([21.0285, 105.8542], 13);

    // Thêm lớp bản đồ OpenStreetMap
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution:
        '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
    }).addTo(map);

    // Thêm marker cho từng xe
    cars.forEach((car) => {
      L.marker([car.lat, car.lng])
        .addTo(map)
        .bindPopup(
          `<b>${car.brand} ${car.model}</b><br>License: ${car.licensePlate}`
        );
    });

    // Cleanup map khi component bị hủy
    return () => {
      map.remove();
    };
  }, [cars]);

  return (
    <div
      id="map"
      ref={mapRef}
      style={{ height: "100vh", width: "100%" }} // Đảm bảo full màn hình
    ></div>
  );
}

export default MapComponent;
