import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import axios from 'axios';

const Map = () => {
  const [carLocations, setCarLocations] = useState([]);

  // Gọi API lấy danh sách xe
  useEffect(() => {
    axios
      .get('/api/cars') // Đảm bảo backend đã chạy và route này hoạt động
      .then((response) => {
        setCarLocations(response.data); // Giả sử API trả về danh sách xe
      })
      .catch((error) => {
        console.error('Error fetching car locations:', error);
      });
  }, []);

  return (
    <MapContainer
      center={[15.5, 108]} // Trung tâm bản đồ tại Việt Nam
      zoom={6}
      style={{ height: '500px', width: '100%' }}
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution="&copy; OpenStreetMap contributors"
      />
      {carLocations.map((car) => (
        <Marker key={car._id} position={[car.latitude, car.longitude]}>
          <Popup>
            <h3>{car.name}</h3>
            <p>Brand: {car.brand}</p>
            <p>Price: {car.price} VND</p>
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default Map;
