import React, { useEffect, useRef } from "react";
import L from "leaflet";

function MapComponent({ selectedCar }) {
  const mapRef = useRef(null); // Khởi tạo ref để tham chiếu đến phần tử div chứa bản đồ

  useEffect(() => {
    // Tạo bản đồ
    const map = L.map(mapRef.current).setView([21.0285, 105.8542], 13); // Vị trí Hà Nội

    // Thêm lớp bản đồ OpenStreetMap
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution:
        '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
    }).addTo(map);

    // Cleanup function để loại bỏ bản đồ khi component bị hủy
    return () => {
      map.remove();
    };
  }, []);

  useEffect(() => {
    if (selectedCar) {
      // Di chuyển đến vị trí của xe đã chọn
      const { lat, lng, brand, model } = selectedCar;
      const popupContent = `<b>${brand} ${model}</b><br />Latitude: ${lat}<br />Longitude: ${lng}`;

      // Tạo marker cho xe được chọn
      L.marker([lat, lng])
        .addTo(mapRef.current._leafletMap)
        .bindPopup(popupContent)
        .openPopup();
      
      // Di chuyển bản đồ đến vị trí của xe
      mapRef.current._leafletMap.setView([lat, lng], 13);
    }
  }, [selectedCar]);

  return <div id="map" ref={mapRef} style={{ width: "100%", height: "100%" }}></div>;
}

export default MapComponent;
