// src/components/MapComponent.js

import React, { useEffect, useRef } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css"; // Đảm bảo import CSS của Leaflet

function MapComponent({ cars }) {
  const mapRef = useRef(null); // Khởi tạo ref để tham chiếu đến div chứa bản đồ

  useEffect(() => {
    // Tạo bản đồ
    const map = L.map(mapRef.current).setView([21.0285, 105.8542], 13); // Vị trí Hà Nội

    // Thêm lớp bản đồ OpenStreetMap
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution:
        '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
    }).addTo(map);

    // Thêm marker từ danh sách xe
    if (cars && cars.length > 0) {
      cars.forEach((car) => {
        L.marker([car.lat, car.lng])
          .addTo(map)
          .bindPopup(
            `<b>${car.brand} ${car.model}</b><br>License Plate: ${car.licensePlate}<br>Price: $${car.pricePerDay}/day`
          );
      });
    }

    // Cleanup function để loại bỏ bản đồ khi component bị hủy
    return () => {
      map.remove();
    };
  }, [cars]); // Chạy lại khi danh sách xe thay đổi

  return (
    <div
      id="map"
      ref={mapRef}
      style={{ height: "100vh", width: "100%" }} // Style cho bản đồ
    ></div>
  );
}

export default MapComponent;
